import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./singlepost.css";

export default function SinglePost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  console.log(path);
  const [post, setPost] = useState({});

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data);
    };
    getPost();
  }, [path]);

  return (
    <div className="singlepost">
      <div className="singlepost-wrapper">
        {post.photo && (
          <img className="singlepost-img" src={post.photo} alt="gambar" />
        )}
        <h1 className="singlepost-title">{post.title}</h1>
        <div className="singlepost-edit">
          <i className="singlepost-icon far fa-edit"></i>
          <i className="singlepost-icon far fa-trash-alt"></i>
        </div>

        <div className="singlepost-info">
          <span className="singlepost-autor">
            Autor: <b>{post.username}</b>
          </span>
          <span className="singlepost-date">
            {new Date(post.createdAt).toDateString()}
          </span>
        </div>
        <p className="singlepost-description">
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Minus amet
          fugiat doloremque iste adipisci excepturi voluptatum voluptates, nulla
          blanditiis distinctio enim animi accusantium ex magnam molestiae dolor
          odit est temporibus tempore at, qui libero magni. Voluptatum natus
          quis cupiditate dicta rerum blanditiis! Iure porro excepturi
          reiciendis pariatur nihil corporis, laudantium veniam ab modi eveniet
          quasi numquam earum atque corrupti cum culpa repellat tempore
          explicabo facere illum obcaecati ea facilis, optio eos. Perspiciatis
          quisquam ipsum saepe libero atque hic, non voluptate quaerat
          aspernatur, omnis quasi laboriosam tempore, quod iste ullam
          repudiandae consectetur deserunt nesciunt? Non odio dicta, voluptatem
          maxime consequuntur eos. Lorem ipsum dolor sit amet, consectetur
          adipisicing elit. Laborum sunt explicabo quasi dignissimos veritatis?
          Quidem quis, quaerat, praesentium iusto minima voluptate est delectus
          recusandae minus hic dolorem assumenda? Soluta vitae molestiae eaque
          quasi, laboriosam esse explicabo voluptates repudiandae quae enim
          quibusdam minus beatae et magni repellendus, aspernatur eveniet saepe
          earum!
        </p>
      </div>
    </div>
  );
}
