import SideBar from "../../components/sidebar/SideBar";
import "./setting.css";
import { useContext, useState } from "react";
import { Context } from "../../context/Context";
import axios from "axios";

export default function Setting() {
  const [file, setFile] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { user } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password,
    };
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newPost.photo = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {
        console.log(err);
      }
    }
    try {
      const res = await axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data._id);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="setting">
      <div className="setting-wrapper">
        <div className="setting-title">
          <span className="setting-title-update">Update Your Account</span>
          <span className="setting-title-delete">Delete Account</span>
        </div>
        <form className="setting-form">
          <label>Profile Picture</label>
          <div className="setting-form-profile">
            <img
              className="setting-profile-img"
              src="https://images.pexels.com/photos/1858175/pexels-photo-1858175.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
              alt="profile"
            />
            <label htmlFor="file-input">
              <i className="setting-profile-icon far fa-user-circle"></i>
            </label>
            <input type="file" id="file-input" style={{ display: "none" }} />
          </div>
          <label>Username</label>
          <input type="text" placeholder="Andes" />
          <label>E-mail</label>
          <input type="email" placeholder="E-mail" />
          <label>Password</label>
          <input type="password" placeholder="Input password here..." />
          <button className="setting-submit">Submit</button>
        </form>
      </div>
      <SideBar />
    </div>
  );
}
